import React, { useState, useEffect } from 'react';
import { TextField, Button, Container, Typography, Box } from '@mui/material';
import { useNavigate, useLocation } from 'react-router-dom';
import './AnalyzeForm.css';

const AnalyzeForm = () => {
  const location = useLocation();
  const { objectiveFunction, constraints, operators, constantes } = location.state || {};
  const [variations, setVariations] = useState({});
  const [loading, setLoading] = useState(true);  // Variável de loading
  const navigate = useNavigate();

  // Atualize o estado de 'loading' quando os dados forem recebidos
  useEffect(() => {
    if (objectiveFunction && constraints && operators && constantes) {
      setLoading(false);  // Altera para false quando os dados chegam
    }
  }, [objectiveFunction, constraints, operators, constantes]);

  // Função para lidar com as mudanças nas variações
  const handleVariationChange = (index, value) => {
    setVariations({ ...variations, [index]: parseFloat(value) });
  };

  // Função para enviar o formulário
  const handleSubmit = (e) => {
    e.preventDefault();
    const data = {
      objectiveFunction,
      constraints,
      operators,
      constantes,
      variations,
    };

    // Envia os dados para o backend
    fetch('http://127.0.0.1:5000/api/analyze', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data),
    })
      .then((response) => response.json())
      .then((data) => {
        // Navega para os resultados após o envio com sucesso
        navigate('/analysis-results', { state: data });
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  };

  // Enquanto os dados não chegam, exibe "Loading..."
  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <Container maxWidth="sm" className="container">
      <Box sx={{ mt: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Análise de Viabilidade
        </Typography>
        <form onSubmit={handleSubmit} className="form-container">
          <Box className="form-section">
            <Typography variant="h6">Variações das Restrições</Typography>
            {constraints.map((constraint, index) => (
              <TextField
                key={index}
                fullWidth
                label={`Variação da Restrição ${index + 1}`}
                variant="outlined"
                value={variations[index] || ''}
                onChange={(e) => handleVariationChange(index, e.target.value)}
                className="form-control"
              />
            ))}
          </Box>
          <Button variant="contained" color="primary" type="submit">
            Analisar
          </Button>
        </form>
      </Box>
    </Container>
  );
};

export default AnalyzeForm;
