import React, { useState } from 'react';

function SimplexForm({ setResults }) {
  const [numVariables, setNumVariables] = useState(2);
  const [numConstraints, setNumConstraints] = useState(2);
  const [objectiveFunction, setObjectiveFunction] = useState([]);
  const [constraints, setConstraints] = useState([]);
  const [bounds, setBounds] = useState([]);
  const [optimizationType, setOptimizationType] = useState('max'); // Novo estado para o tipo de otimização

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch('http://localhost:5000/simplex', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        objectiveFunction,
        constraints,
        bounds,
        optimizationType,
      }),
    });
    const data = await response.json();
    setResults(data);
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Tipo de Otimização:</label>
        <select value={optimizationType} onChange={(e) => setOptimizationType(e.target.value)}>
          <option value="max">Maximizar</option>
          <option value="min">Minimizar</option>
        </select>
      </div>
      <div>
        <label>Número de Variáveis:</label>
        <input
          type="number"
          value={numVariables}
          onChange={(e) => setNumVariables(e.target.value)}
        />
      </div>
      <div>
        <label>Número de Restrições:</label>
        <input
          type="number"
          value={numConstraints}
          onChange={(e) => setNumConstraints(e.target.value)}
        />
      </div>
      <div>
        <label>Função Objetivo:</label>
        <input
          type="text"
          value={objectiveFunction}
          onChange={(e) => setObjectiveFunction(e.target.value.split(',').map(Number))}
        />
      </div>
      <div>
        <label>Restrições:</label>
        <textarea
          value={constraints}
          onChange={(e) => setConstraints(e.target.value.split('\n').map(row => row.split(',').map(Number)))}
        />
      </div>
      <div>
        <label>Limites:</label>
        <textarea
          value={bounds}
          onChange={(e) => setBounds(e.target.value.split('\n').map(Number))}
        />
      </div>
      <button type="submit">Calcular</button>
    </form>
  );
}

export default SimplexForm;
