import React, { useState, useEffect } from 'react';
import { TextField, Button, Container, Typography, Box } from '@mui/material';
import { useNavigate, useLocation } from 'react-router-dom';
import './AnalyzeForm.css'; // Importe o arquivo CSS

const AnalyzeForm = () => {
  const location = useLocation();
  const { objectiveFunction, constraints, operators, constantes } = location.state || {};
  const [variations, setVariations] = useState({});
  const [loading, setLoading] = useState(false); // Estado de carregamento
  const navigate = useNavigate();

  // Verifica se os dados necessários estão disponíveis
  useEffect(() => {
    if (!objectiveFunction || !constraints || !operators || !constantes) {
      console.log('Redirecting to /results due to missing data');
      navigate('/results'); // Redireciona para /results caso algum dado esteja faltando
    }
  }, [objectiveFunction, constraints, operators, constantes, navigate]);

  // Função para lidar com as mudanças nas variações
  const handleVariationChange = (index, value) => {
    setVariations({ ...variations, [index]: parseFloat(value) });
  };

  // Função de submit
  const handleSubmit = (e) => {
    e.preventDefault();
    const data = {
      objectiveFunction,
      constraints,
      operators,
      constantes,
      variations,
    };

    // Envia os dados para o backend
    fetch('http://127.0.0.1:5000/api/analyze', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log('Received analysis from backend:', data);
        
        navigate('/analysis-results', { state: data });
      })
      .catch((error) => {
        
        console.error('Error:', error);
      });
  };

  return (
    <Container maxWidth="sm" className="container">
      <Box sx={{ mt: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Análise de Viabilidade
        </Typography>
        
        {loading ? (
          <Typography variant="h6">Carregando...</Typography> // Exibe "Carregando..." enquanto a requisição está em andamento
        ) : (
          <form onSubmit={handleSubmit} className="form-container">
            <Box className="form-section">
              <Typography variant="h6">Variações das Restrições</Typography>
              {constraints && constraints.map((constraint, index) => (
                <TextField
                  key={index}
                  fullWidth
                  label={`Variação da Restrição ${index + 1}`}
                  variant="outlined"
                  value={variations[index] || ''}
                  onChange={(e) => handleVariationChange(index, e.target.value)}
                  className="form-control"
                />
              ))}
            </Box>
            <Button variant="contained" color="primary" type="submit">
              Analisar
            </Button>
          </form>
        )}
      </Box>
    </Container>
  );
};

export default AnalyzeForm;
