import React, { useState } from 'react';
import { TextField, Button, Container, Typography, Box, MenuItem, Select, FormControl, InputLabel } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const SimplexForm = () => {
  const [objectiveFunction, setObjectiveFunction] = useState('');
  const [constraints, setConstraints] = useState('');
  const [operators, setOperators] = useState([]);
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    const data = {
      objectiveFunction: objectiveFunction.split(',').map(Number),
      constraints: constraints.split('\n').map((row, index) => {
        const values = row.split(',').map(Number);
        return [...values, operators[index]];
      }),
    };
    console.log('Sending data to backend:', data);
    fetch('http://127.0.0.1:5000/api/solve', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log('Received data from backend:', data);
        navigate('/results', { state: data });
      });
  };

  const handleConstraintsChange = (e) => {
    const newConstraints = e.target.value;
    setConstraints(newConstraints);
    const lines = newConstraints.split('\n');
    setOperators(Array(lines.length).fill('<='));
  };

  const handleOperatorChange = (index, value) => {
    const newOperators = [...operators];
    newOperators[index] = value;
    setOperators(newOperators);
  };

  return (
    <Container maxWidth="sm" className="container">
      <Box sx={{ mt: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Calculadora do Método Simplex
        </Typography>
        <form onSubmit={handleSubmit}>
          <Box sx={{ mb: 2 }}>
            <TextField
              fullWidth
              label="Função Objetivo (separada por vírgulas)"
              variant="outlined"
              value={objectiveFunction}
              onChange={(e) => setObjectiveFunction(e.target.value)}
            />
          </Box>
          <Box sx={{ mb: 2 }}>
            <TextField
              fullWidth
              label="Restrições (cada linha uma restrição, valores separados por vírgulas)"
              variant="outlined"
              multiline
              rows={4}
              value={constraints}
              onChange={handleConstraintsChange}
            />
          </Box>
          {constraints.split('\n').map((_, index) => (
            <Box sx={{ mb: 2 }} key={index}>
              <FormControl fullWidth variant="outlined">
                <InputLabel>Operador</InputLabel>
                <Select
                  value={operators[index] || '<='}
                  onChange={(e) => handleOperatorChange(index, e.target.value)}
                  label="Operador"
                >
                  <MenuItem value="<=">&le;</MenuItem>
                  <MenuItem value=">=">&ge;</MenuItem>
                  <MenuItem value="<">&lt;</MenuItem>
                  <MenuItem value=">">&gt;</MenuItem>
                </Select>
              </FormControl>
            </Box>
          ))}
          <Button variant="contained" color="primary" type="submit">
            Calcular
          </Button>
        </form>
        <Button variant="outlined" color="secondary" onClick={handleViewResults} sx={{ mt: 2 }}>
          Ver Resultados de Exemplo
        </Button>
      </Box>
    </Container>
  );
};

export default SimplexForm;
